({buttons:[{xtype:"button",icon:"./themes/common/images/skin/exclamation.png",text:"Alert",itemId:"alert",tooltip:{text:"<b>Alert!</b>"},handler:function(a,b){Ext.Msg.alert("Alert","Alert Button Pressed")}},{xtype:"widgettool",icon:"./themes/common/images/skin/information.png",itemId:"help",handler:function(a,b){Ext.Msg.alert("About","About Button Pressed")}},{type:"gear",itemId:"gear",handler:function(a,b){Ext.Msg.alert("Utility","Utility Button Pressed")}}],menus:[{itemId:"menus",text:"Menus",menu:{items:[{itemId:"addmenu",text:"Add",handler:function(btn,state){var cmp=Ext.getCmp("widgetChrome-example");var msgBox=Ext.Msg.show({title:"Add Menu",msg:"Menu Configuration: ",width:cmp.getWidth()-50,buttons:Ext.Msg.OKCANCEL,defaultTextHeight:cmp.getHeight()-150,multiline:true,fn:function(btn,text){if(btn=="ok"){var data=eval("("+text+");");if(!owfdojo.isArray(data)){data=[data]}OWF.Chrome.addHeaderMenus({items:data})}}});msgBox.textArea.setWidth(msgBox.width-30);msgBox.textArea.setHeight(msgBox.height-100);msgBox.textArea.doComponentLayout()}},{itemId:"insertmenu",text:"Insert",handler:function(btn,state){var cmp=Ext.getCmp("widgetChrome-example");var msgBox=Ext.Msg.show({title:"Insert Menu",msg:"Menu Configuration: ",width:cmp.getWidth()-50,buttons:Ext.Msg.OKCANCEL,defaultTextHeight:cmp.getHeight()-150,multiline:true,fn:function(btn,text){if(btn=="ok"){var data=eval("("+text+");");OWF.Chrome.insertHeaderMenus(data)}}});msgBox.textArea.setWidth(msgBox.width-30);msgBox.textArea.setHeight(msgBox.height-100);msgBox.textArea.doComponentLayout()}},{itemId:"updatemenu",text:"Update",handler:function(btn,state){var cmp=Ext.getCmp("widgetChrome-example");var msgBox=Ext.Msg.show({title:"Update Menu",msg:"Menu Configuration: ",width:cmp.getWidth()-50,buttons:Ext.Msg.OKCANCEL,defaultTextHeight:cmp.getHeight()-150,multiline:true,fn:function(btn,text){if(btn=="ok"){var data=eval("("+text+");");if(!owfdojo.isArray(data)){data=[data]}OWF.Chrome.updateHeaderMenus({items:data})}}});msgBox.textArea.setWidth(msgBox.width-30);msgBox.textArea.setHeight(msgBox.height-100);msgBox.textArea.doComponentLayout()}},{itemId:"removemenu",text:"Remove",handler:function(b,d){var c=Ext.getCmp("widgetChrome-example");var a=Ext.Msg.prompt("Remove Menu","Menu itemId: ",function(f,g){if(f=="ok"){var e=["menus","addmenu","insertmenu","updatemenu","removemenu","listmenu"];if(Ext.Array.indexOf(e,g)==-1){OWF.Chrome.removeHeaderMenus({items:[{itemId:g}]})}else{Ext.Msg.alert("Error","You may not remove that menu.")}}});a.textField.maxWidth=a.width-30;a.textField.doComponentLayout()}},{itemId:"listmenu",text:"List",handler:function(a,c){var b=Ext.getCmp("widgetChrome-example");OWF.Chrome.listHeaderMenus({callback:Ext.bind(function(f){var d=Ozone.util.parseJson(f);var e=d.items.toString().replace(/,/gi,"<br />");Ext.Msg.show({title:"Widget Chrome Menus",msg:e,width:b.getWidth()-50,height:b.getHeight()-150,buttons:Ext.Msg.OK})},b)})}}]}}]});