Ext.define("Ozone.components.admin.stack.StackDetailPanel",{extend:"Ext.panel.Panel",alias:["widget.stackdetailpanel"],viewStack:null,initComponent:function(){this.viewStack=Ext.create("Ext.view.View",{store:Ext.create("Ext.data.Store",{storeId:"storeStackItem",fields:[{name:"name",type:"string"},{name:"description",type:"string"},{name:"stackContext",type:"string"}]}),deferEmptyText:false,tpl:new Ext.XTemplate('<tpl for=".">','<div class="selector">','<div id="detail-info" class="detail-info">','<div class="detail-header-block">','<div class="detail-icon-block">','<div class="detail-title">{name:htmlEncode}</div>',"</div>","</div>",'<div class="detail-block">','<div><span class="detail-label">Stack URL:</span></div>','<div><span class="detail-link">{stackContext:this.renderStackUrl}</span></div>',"</div>",'<div class="detail-block">','<div><span class="detail-label">Description:</span></div>',"<div>{description:htmlEncode}</div>","</div>","</div>","</div>","</tpl>",{compiled:true,renderStackUrl:function(b){b=Ext.htmlEncode(b);var a=OWF.getContainerUrl()+"/#stack="+b;return'<a href="'+a+'" target="_top">'+a+"</a>"}}),emptyText:"No stack selected",itemSelector:"div.selector",autoScroll:"true"});this.items=[this.viewStack];this.callParent(arguments)},loadData:function(a){this.viewStack.store.loadData([a],false)},removeData:function(){this.viewStack.store.removeAll(false)}});