Ext.define("Ozone.components.view.ToolDataView",{extend:"Ozone.components.focusable.FocusableView",alias:"widget.tooldataview",dataField:null,overItemCls:"setting-over",trackOver:true,singleSelect:true,selectedItemCls:"setting-over",itemSelector:"div.tool",store:null,tpl:{},initComponent:function(){this.tpl=Ext.create("Ext.XTemplate",'<tpl for=".">','<div class="tool" tabindex="0" data-qtip="{[Ext.htmlEncode(Ext.htmlEncode(values.name))]}">','<div class="thumb-wrap">','<img src="{image}" class="thumb">',"</div>",'<div class="thumb-text">{[this.encodeAndEllipsize(values.name)]}</div>',"</div>","</tpl>",{encodeAndEllipsize:function(a){return Ext.util.Format.htmlEncode(Ext.Array.map(Ext.Array.erase(/^([\S]+)\s*(.*)?/.exec(Ext.String.trim(a)),0,1),function(b){return Ext.util.Format.ellipsis(b,11)}).join(" "))}});this.on("itemclick",this.onClick);this.on("itemKeyDown",this.onKeyDown);this.on("itemmouseenter",function(b,a,g,d,h,f){var c=b.getSelectionModel();c.select([g])});this.callParent(arguments)},onClick:function(c,a,b){this.up().callBtnHandler(a.data.name,b);return true},onKeyDown:function(b,a,f,c,e,d){switch(e.getKey()){case e.ENTER:case e.SPACE:this.up().callBtnHandler(a.data.name,f,true);return true}}});